import json, sys, subprocess, importlib, time
pkg = 'requests'
try:
    importlib.import_module(pkg)
except ImportError:
    subprocess.check_call([sys.executable, '-m', 'pip', 'install', pkg, '--quiet'])
import requests
method = str(input_data.get('method', 'GET')).upper()
url = str(input_data.get('url', '')).strip()
headers = input_data.get('headers') or {}
params = input_data.get('params') or {}
json_body = input_data.get('json') if 'json' in input_data else None
data_body = input_data.get('data') if 'data' in input_data else None
timeout = float(input_data.get('timeout', 30))
allow_redirects = bool(input_data.get('allow_redirects', True))
verify_tls = bool(input_data.get('verify_tls', True))
allowed = {'GET','POST','PUT','PATCH','DELETE'}
if method not in allowed:
    output = json.dumps({'status':'error','status_code':0,'response_headers':{},'body':'','final_url':'','elapsed_ms':0,'message':'Unsupported method.'}, ensure_ascii=False)
    raise SystemExit
if not url or not (url.startswith('http://') or url.startswith('https://')):
    output = json.dumps({'status':'error','status_code':0,'response_headers':{},'body':'','final_url':'','elapsed_ms':0,'message':'Invalid or missing URL.'}, ensure_ascii=False)
    raise SystemExit
if json_body is not None and data_body is not None:
    output = json.dumps({'status':'error','status_code':0,'response_headers':{},'body':'','final_url':'','elapsed_ms':0,'message':'Provide either json or data, not both.'}, ensure_ascii=False)
    raise SystemExit
if not isinstance(headers, dict) or not isinstance(params, dict):
    output = json.dumps({'status':'error','status_code':0,'response_headers':{},'body':'','final_url':'','elapsed_ms':0,'message':'headers and params must be objects.'}, ensure_ascii=False)
    raise SystemExit
headers = {str(k): str(v) for k, v in headers.items()}
params = {str(k): str(v) for k, v in params.items()}
kwargs = {'headers': headers, 'params': params, 'timeout': timeout, 'allow_redirects': allow_redirects, 'verify': verify_tls}
if json_body is not None:
    kwargs['json'] = json_body
elif data_body is not None:
    kwargs['data'] = data_body
try:
    t0 = time.time()
    resp = requests.request(method, url, **kwargs)
    elapsed_ms = int((time.time() - t0) * 1000)
    resp_headers = {k: v for k, v in resp.headers.items()}
    parsed_json = None
    try:
        parsed_json = resp.json()
    except Exception:
        parsed_json = None
    out = {
        'status': 'success' if resp.ok else 'error',
        'status_code': resp.status_code,
        'response_headers': resp_headers,
        'body': resp.text,
        'final_url': resp.url,
        'elapsed_ms': elapsed_ms,
        'message': '' if resp.ok else f'HTTP {resp.status_code}'
    }
    if parsed_json is not None:
        out['response_json'] = parsed_json
    output = json.dumps(out, ensure_ascii=False)
except Exception as e:
    output = json.dumps({'status':'error','status_code':0,'response_headers':{},'body':'','final_url':'','elapsed_ms':0,'message':str(e)}, ensure_ascii=False)
