import json, sys, subprocess, importlib
pkg = 'pygetwindow'
try:
    importlib.import_module(pkg)
except ImportError:
    subprocess.check_call([sys.executable, '-m', 'pip', 'install', pkg, '--quiet'])
import pygetwindow as gw

title = str(input_data.get('title', '')).strip()
exact = bool(input_data.get('exact', False))
index = int(input_data.get('index', 0))

try:
    if title:
        windows = gw.getWindowsWithTitle(title) if not exact else [w for w in gw.getAllWindows() if w.title == title]
        if not windows:
            output = json.dumps({'status': 'error', 'matched_title': '', 'message': 'No matching window found.'})
            sys.exit()
        if index < 0 or index >= len(windows):
            output = json.dumps({'status': 'error', 'matched_title': '', 'message': 'index out of range.'})
            sys.exit()
        win = windows[index]
    else:
        win = gw.getActiveWindow()
        if win is None:
            output = json.dumps({'status': 'error', 'matched_title': '', 'message': 'No active window to minimize.'})
            sys.exit()
    win.minimize()
    output = json.dumps({'status': 'success', 'matched_title': win.title, 'message': ''})
except Exception as e:
    output = json.dumps({'status': 'error', 'matched_title': '', 'message': str(e)})
